(13519, 1180, '_form', '<div class=\"form_wrapper\">\n    <h4 class=\"mb-30 w-100\">First Amortization</h4>\n    <p class=\"mb-30 w-100\">Were Your Loan Payment Updated?</p>\n    [radio first_amortization class:mb-30 use_label_element \"Yes\" \"No\"]\n</div>\n\n[group yes]\n<div class=\"form_wrapper\">\n    <div class=\"form_field\">\n        [text* date_of_payment id:date_picker autocomplete:off class:required placeholder \"Date of Payment\n        (Required)\"]\n        [number* receipt_reference class:required autocomplete:off placeholder \"Acknowledgment Receipt Reference\n        Number (Required)\"]\n    </div>\n    [number* amount class:required placeholder \"Amount (Required)\"]\n</div>\n[/group][cf7mls_step cf7mls_step-1 \"Next\" \"\"]<div class=\"form_wrapper\">\n    <div class=\"row\">\n        <div class=\"col-md-12\">\n            <div class=\"header\">\n                <h4>Loan Payment</h4>\n            </div>\n        </div>\n        <div class=\"col-md-12\">\n            <div class=\"field mb-30\">\n                <p>Were Your Loan Payment Updated?</p>\n                [radio loan_payment_updated use_label_element \"Yes\" \"No\"]\n            </div>\n        </div>\n    </div>\n</div>\n\n[group loan_payment]\n    <div class=\"form_wrapper\">\n        <div class=\"row\" style=\"gap: 24px 0;\">\n            <div class=\"col-md-6\">\n                [text* full_name placeholder \"Account Name (Required)\"]\n            </div>\n            <div class=\"col-md-6\">\n                [number* account_number placeholder \"Account Number (Required)\"]\n            </div>\n            <div class=\"col-md-6\">\n                [tel* mobile_number placeholder \"Mobile Number (Required)\"]\n            </div>\n            <div class=\"col-md-6\">\n                [email* email_address placeholder \"Email Address (Required)\"]\n            </div>\n            <div class=\"col-md-12\">\n                [text* present_residential_address placeholder \"Present Residential Address (Required)\"]\n            </div>\n            <div class=\"col-md-6\">\n                [text* original_due_date placeholder \"Original Due Date (Required)\"]\n            </div>\n            <div class=\"col-md-6\">\n                [text* new_due_date placeholder \"New Due Date (Required)\"]\n            </div>\n            <div class=\"col-md-12\">\n                [select* first_as_label reason \"Reason (Required)\" \"Change of Payroll Schedule\" \"Change of Job or Employment\" \"Change of Remittance Schedule\" \"Others\"]\n            </div>\n            <div class=\"col-md-12\">\n                [submit class:orange_btn id:submit_btn \"Submit\"]\n            </div>\n        </div>\n    </div>\n[/group]\n[cf7mls_step cf7mls_step-2 \"Back\" \"Step 2\"]'),
