(13607, 1188, '_form', '<div class=\"step1\">\n<h3>Business Information</h3>\n   <div class=\"input_rows\">\n        [select* application_type class:application_type first_as_label \"Application Type (Required)\" \"New Application\" \"Additional Loan\" \"Renewal\" \"Restructuring\"]\n        [select* type_borrower class:type_borrower first_as_label \"Type of Borrower (Required)\" \"Cooperative\" \"Partnership\" \"One-Person Corporation\" \"Corporation\"]\n    </div>\n\n    [text* reg_business_name class:reg_business_name placeholder \"Registered Business Name (Required)\"]\n\n    [text* principal_bname class:principal_bname placeholder \"Principal Business Name (Required): Unit #, Building / House #, Street, Subdivision/ Barangay/ District, Municipality/ City, Province, Zip Code\"]\n\n    <div class=\"input_rows\">\n        [number* tin_no class:tin_no placeholder \"TIN (Required)\"]\n        [text* website_social class:website_social placeholder \"Website/Social Media (Required)\"]\n    </div>\n\n    <div class=\"input_rows\">\n        [select* business_add_ownership class:business_add_ownership first_as_label \"Business Address Ownership (Required)\" \"Owned (Unencumbered)\" \"Owned (mortgaged)\" \"Rented\"]\n        [number* years_operate class:years_operate placeholder \"Years the Business has been in operation (Required)\"]\n    </div>\n\n    <div class=\"input_rows\">\n        [number* no_branches class:no_branches placeholder \"Number of Branches (Required)\"]\n        [number* no_subsidiaries class:no_subsidiaries placeholder \"Number of Subsidiaries (Required)\"]\n    </div>\n\n    <div class=\"input_rows\">\n        [text* nature_business class:nature_business placeholder \"Nature of Business (Required)\"]\n        [text* business_activity class:business_activity placeholder \"Please specify business activity (Required)\"]\n    </div>\n\n<span class=\"d-block blue_text\">Business Registration 1</span>\n<div class=\"input_rows\">\n    [text dti1 class:dti class:corpo_bus_reg readonly \"CDA\"]\n    [number reg_number1 class:reg_number1 class:reg_number placeholder \"Registration Number\"]\n</div>\n<div class=\"input_rows\">\n    [text business_reg_date1 class:business_reg_date1 class:datepicker placeholder \"Date of Business Registration (mm/dd/yyyy)\"]\n    [text exp_reg_date1 class:exp_reg_date1 class:datepicker placeholder \"Expiry Date of Registration (mm/dd/yyyy)\"]\n</div>\n\n<span class=\"d-block blue_text\">Business Registration 2</span>\n<div class=\"input_rows\">\n    [text dti2 class:dti class:corpo_bus_reg readonly \"DTI\"]\n    [number reg_number2 class:reg_number2 class:reg_number placeholder \"Registration Number\"]\n</div>\n<div class=\"input_rows\">\n    [text business_reg_date2 class:business_reg_date2 class:datepicker placeholder \"Date of Business Registration (mm/dd/yyyy)\"]\n    [text exp_reg_date2 class:exp_reg_date2 class:datepicker placeholder \"Expiry Date of Registration (mm/dd/yyyy)\"]\n</div>\n\n<span class=\"d-block blue_text\">Business Registration 3</span>\n<div class=\"input_rows\">\n    [text sec class:sec class:corpo_bus_reg readonly \"SEC\"]\n    [number reg_number3 class:reg_number3 class:reg_number placeholder \"Registration Number\"]\n</div>\n<div class=\"input_rows\">\n    [text business_reg_date3 class:business_reg_date3 class:datepicker placeholder \"Date of Business Registration (mm/dd/yyyy)\"]\n    [text exp_reg_date3 class:exp_reg_date3 class:datepicker placeholder \"Expiry Date of Registration (mm/dd/yyyy)\"]\n</div>\n\n<span class=\"d-block blue_text\">Business Registration 4</span>\n<div class=\"input_rows\">\n    [text bir class:bir class:corpo_bus_reg readonly \"BIR\"]\n    [number reg_number4 class:reg_number4 class:reg_number placeholder \"Registration Number\"]\n</div>\n<div class=\"input_rows\">\n    [text business_reg_date4 class:business_reg_date4 class:datepicker placeholder \"Date of Business Registration (mm/dd/yyyy)\"]\n    [text exp_reg_date4 class:exp_reg_date4 class:datepicker placeholder \"Expiry Date of Registration (mm/dd/yyyy)\"]\n</div>\n\n<span class=\"d-block blue_text\">Business Registration 5</span>\n<div class=\"input_rows\">\n    [text brgy_permit class:brgy_permit class:corpo_bus_reg readonly \"Barangay/Mayor’s Permit\"]\n    [number reg_number5 class:reg_number5 class:reg_number placeholder \"Registration Number\"]\n</div>\n<div class=\"input_rows\">\n    [text business_reg_date5 class:business_reg_date5 class:datepicker placeholder \"Date of Business Registration (mm/dd/yyyy)\"]\n    [text exp_reg_date5 class:exp_reg_date5 class:datepicker placeholder \"Expiry Date of Registration (mm/dd/yyyy)\"]\n</div>\n\n<span class=\"d-block blue_text\">Business Registration 6</span>\n<div class=\"input_rows\">\n    [text others class:others class:corpo_bus_reg placeholder \"Others (Please specify):\"]\n    [number reg_number6 class:reg_number6 class:reg_number placeholder \"Registration Number\"]\n</div>\n<div class=\"input_rows\">\n    [text business_reg_date6 class:business_reg_date6 class:datepicker placeholder \"Date of Business Registration (mm/dd/yyyy)\"]\n    [text exp_reg_date6 class:exp_reg_date6 class:datepicker placeholder \"Expiry Date of Registration (mm/dd/yyyy)\"]\n</div>\n\n    [select* indicate class:indicate first_as_label \"Indicate whether the business has (Required)\" \"Is at least 51% (majority) owned by female/s\" \"Is at least 20% owned by female/s; AND (i) has at least 1 woman as CEO/COO/President/Vice President;\n    AND (ii) 30% of directors composed of women, where a board exists\"]\n\n    [select* firm_size class:firm_size first_as_label \"Firm Size (Required): Total assets exclusive of the land on which the business entity’s office, plant and equipment are situated\" \"Micro (not more than Php 3M)\" \"Small (Php3,000,001 to 15M)\" \"Medium (Php15,000,001 to 100M)\"]\n\n    [number* sales_revenue class:sales_revenue placeholder \"Annual Sales or Revenue (Required)\"]\n\n    <div class=\"input_rows\">\n        [number* sales_fulltime class:sales_fulltime placeholder \"Number of Sales Full-time (Required)\"]\n        [number* sales_parttime class:sales_parttime placeholder \"Number of Sales Part-time/Contractual (Required)\"]\n    </div>\n</div>\n[cf7mls_step cf7mls_step-1 \"Next\" \"\"]<div class=\"step2\">\n\n<h3 class=\"mb-30\">Contact Information</h3>\n\n<span class=\"d-block black_text\">Authorized Representative 1</span>\n\n[text* contact_your_name1 class:contact_your_name1 placeholder \"Contact Name (Required)\"]\n\n<div class=\"input_rows\">\n    [text* contact_government_id1 class:contact_government_id1 placeholder \"Contact Government ID (Required)\"]\n[text* contact_birth_date1 class:contact_birth_date1 class:datepicker placeholder \"Contact Date of Birth (mm/dd/yyyy)\"]\n</div>\n\n<div class=\"input_rows\">\n    [tel* contact_mobile_number1 class:contact_mobile_number1 placeholder \"Contact Mobile Number (Required)\"]\n[tel* contact_landline_number1 class:contact_landline_number1 placeholder \"Contact Landline No. (Required)\"]\n</div>\n\n<div class=\"input_rows\">\n    [email* contact_your_email1 class:contact_your_email1 placeholder \"Contact Email Address (Required)\"]\n[select* contact_gender1 class:contact_gender1 first_as_label \"Gender (Required)\" \"Male\" \"Female\"]\n</div>\n\n<span class=\"d-block black_text\">Authorized Representative 2</span>\n\n[text* contact_your_name2 class:contact_your_name2 placeholder \"Contact Name (Required)\"]\n\n<div class=\"input_rows\">\n    [text* contact_government_id2 class:contact_government_id2 placeholder \"Contact Government ID (Required)\"]\n[text* contact_birth_date2 class:contact_birth_date2 class:datepicker placeholder \"Contact Date of Birth (mm/dd/yyyy)\"]\n</div>\n\n<div class=\"input_rows\">\n    [tel* contact_mobile_number2 class:contact_mobile_number2 placeholder \"Contact Mobile Number (Required)\"]\n[tel* contact_landline_number2 class:contact_landline_number2 placeholder \"Contact Landline No. (Required)\"]\n</div>\n\n<div class=\"input_rows\">\n    [email* contact_your_email2 class:contact_your_email2 placeholder \"Contact Email Address (Required)\"]\n[select* contact_gender2 class:contact_gender2 first_as_label \"Gender (Required)\" \"Male\" \"Female\"]\n</div>\n\n<h3 class=\"mb-30\">Top Trade References</h3>\n\n<span class=\"d-block blue_text\">Top Suppliers 1</span>\n<div class=\"input_rows\">\n  [text top_supplier1 class:top_supplier1 placeholder \"Name of Top Suppliers\"]\n  [text supplied_service1 class:supplied_service1 placeholder \"Goods Supplied/Services Rendered\"]\n</div>\n\n<div class=\"input_rows\">\n  [text contact_person1 class:contact_person1 placeholder \"Contact Person\"]\n  [tel contact_number1 class:contact_number1 class:ref_contact placeholder \"Contact Number\"]\n</div>\n\n<span class=\"d-block blue_text\">Top Suppliers 2</span>\n<div class=\"input_rows\">\n  [text top_supplier2 class:top_supplier2 placeholder \"Name of Top Suppliers\"]\n  [text supplied_service2 class:supplied_service2 placeholder \"Goods Supplied/Services Rendered\"]\n</div>\n\n<div class=\"input_rows\">\n  [text contact_person2 class:contact_person2 placeholder \"Contact Person\"]\n  [tel contact_number2 class:contact_number2 class:ref_contact placeholder \"Contact Number\"]\n</div>\n\n<span class=\"d-block blue_text\">Top Suppliers 3</span>\n<div class=\"input_rows\">\n  [text top_supplier3 class:top_supplier3 placeholder \"Name of Top Suppliers\"]\n  [text supplied_service3 class:supplied_service3 placeholder \"Goods Supplied/Services Rendered\"]\n</div>\n\n<div class=\"input_rows\">\n  [text contact_person3 class:contact_person3 placeholder \"Contact Person\"]\n  [tel contact_number3 class:contact_number3 class:ref_contact placeholder \"Contact Number\"]\n</div>\n\n<span class=\"d-block blue_text\">Top Customers 1</span>\n<div class=\"input_rows\">\n  [text top_customer1 class:top_customer1 placeholder \"Name of Top Customers\"]\n  [text purchased_service1 class:purchased_service1 placeholder \"Goods Purchased/Services Availed\"]\n</div>\n\n<div class=\"input_rows\">\n  [text contact_person4 class:contact_person4 placeholder \"Contact Person\"]\n  [tel contact_number4 class:contact_number4 class:ref_contact placeholder \"Contact Number\"]\n</div>\n\n<span class=\"d-block blue_text\">Top Customers 2</span>\n<div class=\"input_rows\">\n  [text top_customer2 class:top_customer2 placeholder \"Name of Top Customers\"]\n  [text purchased_service2 class:purchased_service2 placeholder \"Goods Purchased/Services Availed\"]\n</div>\n\n<div class=\"input_rows\">\n  [text contact_person5 class:contact_person5 placeholder \"Contact Person\"]\n  [tel contact_number5 class:contact_number5 class:ref_contact placeholder \"Contact Number\"]\n</div>\n\n<span class=\"d-block blue_text\">Top Customers 3</span>\n<div class=\"input_rows\">\n  [text top_customer3 class:top_customer3 placeholder \"Name of Top Customers\"]\n  [text purchased_service3 class:purchased_service3 placeholder \"Goods Purchased/Services Availed\"]\n</div>\n\n<div class=\"input_rows\">\n  [text contact_person6 class:contact_person6 placeholder \"Contact Person\"]\n  [tel contact_number6 class:contact_number6 class:ref_contact placeholder \"Contact Number\"]\n</div>\n\n\n</div>[cf7mls_step cf7mls_step-2 \"Back\" \"Next\" \"Step 2\"]<div class=\"step3\">\n\n<h3>Loan Application Information</h3>\n\n<div class=\"input_rows\">\n  [text* loan_amount class:loan_amount placeholder \"Loan amount applied for (Required): subject to the approval of the bank\"]\n  [number* tenor class:tenor placeholder \"Tenor (Required): months\"]\n</div>\n\n<div class=\"input_rows\">\n  [select* loan_frequency class:loan_frequency first_as_label \"Proposed Frequency of Repayment (Required)\" \"Weekly\" \"Monthly\" \"Quarterly\" \"Annually\" \"Lump sum\" \"Others (Please specify)\"]\n  [text others1 class:others1 class:loan_others1 readonly placeholder \"Others (please specify)\"]\