                    <div class="menu_list">
                        <?php
                        $terms = get_terms([
                            'taxonomy' => 'deposit-rates',
                            'hide_empty' => false,
                        ]);

                        if (!is_wp_error($terms) && !empty($terms)) {
                            foreach ($terms as $term) {
                                $term_link = get_term_link($term);
                                $term_name = $term->name;
                                $icon_url = get_field('icon', $term); // ACF returns URL

                                echo '<a href="' . esc_url($term_link) . '" rel="noopener noreferrer">';

                                if ($icon_url) {
                                    // Convert URL to server path
                                    $upload_dir = wp_upload_dir();
                                    $relative_path = str_replace($upload_dir['baseurl'], '', $icon_url);
                                    $svg_path = $upload_dir['basedir'] . $relative_path;

                                    if (file_exists($svg_path) && pathinfo($svg_path, PATHINFO_EXTENSION) === 'svg') {
                                        echo file_get_contents($svg_path); // Output raw SVG
                                    }
                                }

                                echo esc_html($term_name) . '</a>';
                            }
                        }
